template_markup:
{% set block_id = block.id or block.order_index %}
{% set gradient_start = gradient_start or "#312e81" %}
{% set gradient_end = gradient_end or "#111827" %}
{% set text_color = card_text_color or "#ffffff" %}
{% set badge_color = badge_color or "#f472b6" %}
<section class="aurora-module" data-block-section="{{ block_id }}">
  <div class="aurora-card" style="background:linear-gradient(135deg, {{ gradient_start }}, {{ gradient_end }});color:{{ text_color }};">
    {% if badge_label %}
      <span class="aurora-badge" style="background:{{ badge_color }};">
        {{ helpers.text("badge_label", tag="span") }}
      </span>
    {% endif %}
    {{ helpers.text("eyebrow", tag="p", classes="aurora-eyebrow") }}
    {{ helpers.text("headline", tag="h2", classes="aurora-headline") }}
    {{ helpers.richtext("description", tag="p", classes="aurora-description") }}
    <div class="aurora-cta">
      <a class="primary" href="{{ primary_cta_url or "#" }}">
        {{ helpers.text("primary_cta_label", tag="span") }}
      </a>
      <a class="ghost" href="{{ secondary_cta_url or "#" }}">
        {{ helpers.text("secondary_cta_label", tag="span") }}
      </a>
    </div>
    <div class="aurora-stats">
      {% for stat in stats %}
        <article>
          {{ helpers.item_text(stat, "value", tag="strong") }}
          {{ helpers.item_text(stat, "label", tag="span") }}
        </article>
      {% endfor %}
    </div>
  </div>
  <div class="aurora-media" data-shape="{{ (image_shape or "rounded") | lower }}">
    {{ helpers.asset("image_url", classes="aurora-media-img", label="Hero media") }}
  </div>
</section>

template_styles:
[data-template-key="aurora-showcase"] .aurora-module {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 24px;
  align-items: stretch;
}
[data-template-key="aurora-showcase"] .aurora-card {
  padding: 32px;
  border-radius: 28px;
  display: flex;
  flex-direction: column;
  gap: 16px;
  box-shadow: 0 25px 50px rgba(15, 23, 42, 0.4);
}
[data-template-key="aurora-showcase"] .aurora-badge {
  align-self: flex-start;
  padding: 6px 16px;
  border-radius: 999px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: #0f172a;
}
[data-template-key="aurora-showcase"] .aurora-headline {
  margin: 0;
  font-size: 2.4rem;
  line-height: 1.2;
}
[data-template-key="aurora-showcase"] .aurora-description {
  opacity: 0.9;
  line-height: 1.6;
}
[data-template-key="aurora-showcase"] .aurora-cta {
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
}
[data-template-key="aurora-showcase"] .aurora-cta .primary,
[data-template-key="aurora-showcase"] .aurora-cta .ghost {
  border-radius: 999px;
  padding: 10px 20px;
  font-weight: 600;
  text-decoration: none;
}
[data-template-key="aurora-showcase"] .aurora-cta .primary {
  background: #f472b6;
  color: #0f172a;
}
[data-template-key="aurora-showcase"] .aurora-cta .ghost {
  border: 1px solid rgba(255, 255, 255, 0.5);
  color: inherit;
}
[data-template-key="aurora-showcase"] .aurora-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 12px;
}
[data-template-key="aurora-showcase"] .aurora-stats article {
  border-radius: 18px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  padding: 12px;
  text-align: center;
  background: rgba(255, 255, 255, 0.08);
}
[data-template-key="aurora-showcase"] .aurora-media figure {
  margin: 0;
  position: relative;
  overflow: hidden;
  border-radius: 32px;
  box-shadow: 0 30px 80px rgba(15, 23, 42, 0.35);
}
[data-template-key="aurora-showcase"] .aurora-media[data-shape="circle"] figure {
  border-radius: 999px;
}
[data-template-key="aurora-showcase"] .aurora-media img,
[data-template-key="aurora-showcase"] .aurora-media video {
  width: 100%;
  display: block;
  height: auto;
}
